def what_action
  puts "        Quel actions voulez vous faire ?"
  puts " > Action 1 : affiche le nombre de handle de l' array"
  puts " > Action 2 : Quel est le handle le plus court ?"
  puts " > Action 3 : Combien y a-t-il de handle de 5 caractères ?"
  puts " > Action 4 : Combien de handles commencent par une majuscule ?"
  puts " > Action 5 : Afficher/trier la array par ordre alphabétique de handle"
  puts " > Action 6 : Afficher/trier la array par ordre croissant de taille de handle"
  puts " > Action 7 : Quel est la position de l'handle @epenser"
  puts " > Action 8 : Affiche par groupe de taille les handle"
  print " > "
  input = gets.chomp.to_i
  while (input < 1  || input > 8)
    puts "Choississez un nombre comporis entre 1 et 8 !"
    input = gets.chomp.to_i
  end
  puts " > Action #{input} séléctionné !"
  return (input)
end

def show_length array_twitter
  puts "L'array contient #{array_twitter.length} handles."
end

def show_smallest_handle array_twitter
  value = array_twitter.min { |a, b| a.length <=> b.length }
  puts "Le Handle le plus court est #{value}."
end


def how_many_uppercase array_twitter
  value = array_twitter.count {|x| x[1] >= 'A' && x[1] <= 'Z'}
  puts "Il y a #{value} handles qui commencent par une majuscule."
end

def how_many_5c_long_handle array_twitter
  value = array_twitter.count {|x| x.length == 6}
  puts "Il y a #{value} handles de 5 caractères."
end

def show_sort_alpha array_twitter
  array_twitter = array_twitter.sort
  puts array_twitter
end

def show_sort_size array_twitter
  array_twitter = array_twitter.sort {|a, b| a.length <=> b.length}
  puts array_twitter
end

def show_epenser array_twitter
  value = array_twitter.index("@epenser")
  puts "La position de l'handle @epenser est #{value}."
end

def show_by_size array_twitter
  array_twitter = array_twitter.sort {|a, b| a.length <=> b.length}
  position = 0
  while position < array_twitter.length
    size = array_twitter[position].length
    count = 0
    puts "<<< #{size - 1} caractères >>>"
    while (position < array_twitter.length) && (array_twitter[position].length == size) 
      count += 1
      position += 1
    end
    puts "    #{count} handle    "
  end
end

